.scroll-top-btn {
  position: fixed;
  bottom: 2rem;
  right: -8rem;
  font-size: 3rem;
  transform: rotate(270deg);
  background-color: black;
  color: white;
  border-radius: 50%;
  padding: 0rem 1.5rem;
  z-index: 60;
  cursor: pointer;
  box-shadow: 1px 1px 10px -5px black;
  transition: all 1s ease-out;
}
.slide-in {
  right: 3rem;
  transition: all 0.5s ease-out;
  &:hover {
    background-color: white;
    color: black;
    box-shadow: 1px 1px 10px -5px black;
  }
}

.main-nav-for-mobile {
  position: fixed;
  z-index: 80;
  background: white;
  width: 100%;
  height: 90vh;
  display: flex;
  justify-content: center;
  list-style: none;
  align-items: center;
  flex-direction: column;
  bottom: 100%;
  & > a {
    font-size: 3rem;
    padding: 3rem;
    color: black;
    text-decoration: none;
    opacity: 0;
    transform: translate(0px, 20px);
    transition: all 0.4s ease-out;
    &:hover {
      color: rgb(244 94 94 / 95%);
    }
  }
}
.main-nav-for-mobile-show {
  display: flex;
  bottom: 0;
  transition: all 3s ease-in;
}
main {
  ///poster
  & .main-poster {
    height: 100vh;
    width: 100%;
    &-continer {
      height: 100%;
      position: relative;
      & > img {
        height: 100%;
        width: 100%;
        object-fit: cover;
        position: absolute;
      }
      & > div {
        position: absolute;
        width: 100%;
        height: 100%;
        background-color: rgb(0 0 0 / 56%);
        & > div {
          position: absolute;
          left: 15%;
          top: 50%;
          color: white;
          transform: translate(-10%, -50%);
          font-size: 3.8rem;
          font-weight: 600;
          line-height: 1.4;
          & > * {
            opacity: 0;
          }
          @media (max-width: ($tabletmedia)) {
            font-size: 3.3rem;
          }
          @media (max-width: ($mobilemediamidm)) {
            font-size: 3rem;
          }
          @media (max-width: ($mobilemediamidm3 )) {
            font-size: 2.8rem;
          }

          & > a {
            padding: 1rem;
            background: white;
            color: $textColor;
            border-radius: 40px;
            font-size: 1.9rem;
            text-decoration: none;
            transition: all 0.5s ease-out;
            box-shadow: 1px 1px 10px -5px black;
            &:hover {
              background-color: $textColor;
              color: white;
            }
          }
        }
      }
    }
  }

  /// about us
  & .main-about-us {
    margin: 12rem 2rem;
    font-size: 2.2rem;
    color: $textColor;

    & > div {
      text-align: center;
      width: 70%;
      margin: 0 auto;
      @media (max-width: ($laptopmedia)) {
        width: 90%;
      }
      @media (max-width: ($mobilemedialarg)) {
        width: 100%;
      }
      & > h1 {
        margin: 2rem 0rem;
        font-size: 4rem;
      }
      & > h3 {
        margin: 1rem 0;
      }
      & > p {
        margin: 3rem 0;
        & > span {
          color: #ef6c77;
        }
      }
    }
  }

  & .main-products {
    margin: 4rem 1rem;
    font-size: 2rem;

    &-title {
      font-size: 4rem;
      text-align: center;
    }
    &-grid-wapper {
      & > div {
        display: flex;
        align-items: center;
        margin: 4rem;
        padding: 1rem;
        box-shadow: 1px 2px 20px -5px black;

        @media (max-width: ($laptopmediamidm)) {
          margin: 2rem;
        }
        @media (max-width: ($mobilemedialarg)) {
          flex-direction: column;
        }
        & > .main-products-grid-wapper-content {
          font-size: 2rem;
          flex: 1;
          padding: 2rem 4rem;
          color: white;
          @media (max-width: ($laptopmediamidm)) {
            padding: 2rem 2rem;
          }
          @media (max-width: ($mobilemedialarg)) {
            order: 2;
          }
          & > p {
            margin: 1rem 0;
          }
          & > h2 {
            margin: 2rem 0;
            font-size: 3.6rem;
          }
        }
        & > .main-products-grid-wapper-img {
          width: 40%;
          flex: 1;
          position: relative;
          overflow: hidden;

          @media (max-width: ($mobilemedialarg)) {
            width: 52%;
          }
          @media (max-width: ($mobilemediamidm)) {
            width: 70%;
          }
          & > .powder-img {
            position: absolute;
            left: 0;
            top: 0;
            z-index: 4;
          }
          & > img {
            width: 100%;
            box-shadow: 1px 1px 10px -5px black;
            height: 30vw;
            border-radius: 3px;
            object-fit: cover;
            z-index: 5;
            position: relative;
            left: 0;
            @media (max-width: ($mobilemedialarg)) {
              height: 40vw;
            }
          }
        }
      }
    }
  }

  ///map
  .main-map {
    margin: 7rem 1rem;
    height: 70vh;
    text-align: center;
    & > a > img {
      height: 100%;
      width: 80%;
      box-shadow: 1px 1px 10px -5px black;
    }
  }

  /// contact us
  .contact-wapper {
    margin: 2rem 1rem;
    font-size: 2.5rem;
    @media (max-width: ($laptopmediamidm)) {
      font-size: 2.2rem;
    }

    &-title {
      font-size: 4rem;
      text-align: center;
    }
    &-grid {
      display: grid;
      grid-template-columns: repeat(auto-fit, minmax(500px, 1fr));
      grid-gap: 3rem;
      margin: 4rem 2rem;
      align-items: center;
      #grid-inner-flex {
        flex-direction: column;
        & > #grid-inner-flex-item {
          display: flex;
          align-items: center;
          width: 100%;
          flex-wrap: wrap;
          @media (max-width: ($mobilemediamidm4)) {
            justify-content: center;
            margin: 3rem 0;
          }
          @media (max-width: ($tabletmedia)) {
            justify-content: center;
          }

          & > * {
            margin: 0 2rem;
            @media (max-width: ($mobilemediamidm4)) {
              margin: 0;
            }
          }
          & > .svgs-w {
            height: 4rem;
            margin: 0 0.5rem;
          }
        }
      }
      @media (max-width: ($laptopmedia)) {
        grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
      }
      @media (max-width: ($mobilemediamidm3 )) {
        grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
      }
      & > div {
        display: flex;
        display: flex;
        justify-content: space-around;
        align-items: center;
        flex-wrap: wrap;

        & > * {
          margin: 2rem;
          @media (max-width: ($mobilemediamidm4)) {
            margin: 3rem 0;
          }
        }
      }
    }
  }
}
.btom-logo {
  width: 100%;
  text-align: center;
  & > img {
    width: 50%;
  }
}

.website-content {
  margin: 0;
  display: flex;
  width: 100%;
  justify-content: center;
  align-items: center;
  flex-wrap: wrap;
  & > p {
    margin: 0 2rem;
  }
}
footer {
  padding: 2rem;
  background: black;
  color: #ffffffc2;
  font-size: 1.5rem;
  text-align: center;
}

#svgs {
  width: 5rem;
  height: 6rem;
}

.p1-color {
  background-color: #156064;
}
.p2-color {
  background-color: #04a280;
}
.p3-color {
  background: #3d799c;
}

.p4-color {
  background-color: #e5a597;
}
