main {
  ///poster
  & .main-poster {
    height: 100vh;
    width: 100%;
    &-continer {
      height: 100%;
      position: relative;
      & > img {
        height: 100%;
        width: 100%;
        object-fit: cover;
        position: absolute;
      }
      & > div {
        position: absolute;
        width: 100%;
        height: 100%;
        background-color: rgb(0 0 0 / 56%);
        & > div {
          position: absolute;
          left: 15%;
          top: 50%;
          color: white;
          transform: translate(-10%, -50%);
          font-size: 3.8rem;
          font-weight: 600;
          line-height: 1.4;
          & > * {
            opacity: 0;
          }
          @media (max-width: ($tabletmedia)) {
