  & .main-about-us {
    margin: 12rem 2rem;
    font-size: 2.2rem;
    color: $textColor;

    & > div {
      text-align: center;
      width: 70%;
      margin: 0 auto;
      @media (max-width: ($laptopmedia)) {
        width: 90%;
      }
      @media (max-width: ($mobilemedialarg)) {
        width: 100%;
